import React, { Component } from 'react';
import * as d3 from "d3";

class Axis extends Component {
  constructor(props) {
    super(props);
    this.groupRef = React.createRef();
  }

  componentDidMount() {
    this.drawAxis()
  }

  drawAxis() {
    let xScale = d3.scaleLinear()
      .domain([0, this.props.maxX])
      .range([this.props.padding, this.props.width - this.props.padding]);
    let yScale = d3.scaleLinear()
      .domain([0, this.props.maxY])
      .range([this.props.height - this.props.padding, this.props.padding]);
    let axis, xTranslate, yTranslate

    if (this.props.dimension === `x`) {
      axis = d3.axisBottom(xScale)
      xTranslate = 0
      yTranslate = this.props.dimension - this.props.padding
    } else if (this.props.type === `y`) {
      axis = d3.axisLeft(yScale)
      xTranslate = this.props.padding
      yTranslate = 0
    }

    /* eslint-disable no-console */
    console.log(this.groupRef)

    d3.select(this.groupRef.current)
      .attr(`transform`, `translate(${xTranslate}, ${yTranslate})`)
      .call(axis);
  }

  render() {
    return (
      <g ref={this.groupRef}></g>
    );
  }

}

export default Axis;
