import React, { Component } from "react";
import Axis from "../Axis/Axis";
import DataVisulisation from "../DataVisulisation/DataVisulisation";
import Regression from "../Regression/Regression";
import Legend from "../Legend/Legend";
import { Scales } from "../utils/Scales";

class ScatterPlot extends Component {
  constructor(props) {
    super(props);
    this.height = 400;
    this.padding  = 50;
    this.width= 400;
    this.scalesInstance = Scales(this.width, this.height, this.padding, this.props.data);
    this.colors = [`aqua`, `red`, `green`];
    this.datasets = this.getDataInfo();
  }

  /**
   * @return {object}
   */
  getDataInfo() {
    const info = {}

    Object.keys(this.props.data).forEach((key, i) => {
      info[key] = {
        color: this.colors[i],
        data: this.props.data[key]
      };
    });
    return info;
  }

  /**
   * @return {array}
   */
  renderData() {
    const vis = []

    Object.keys(this.datasets).forEach((key, i) => {
      const dataVis = <DataVisulisation key={`data-${key}`} data={this.datasets[key].data}
        scalesInstance={this.scalesInstance} name={key} color={this.datasets[key].color} />;
      const regression = <Regression key={`regression-${key}`} data={this.datasets[key].data}
        scalesInstance={this.scalesInstance} color={this.datasets[key].color} />;

      vis.push(dataVis);
      vis.push(regression);
    });

    return vis;
  }

  render() {
    return (
      <svg width={this.width} height={this.height} style={{background: "grey"}}>
        <Axis scale={this.xScale} dimension="x" height={this.height} padding={this.padding}
          scalesInstance={this.scalesInstance} />
        <Axis scale={this.yScale} dimension="y" height={this.height} padding={this.padding}
          scalesInstance={this.scalesInstance} />
        {this.renderData()}
        <Legend datasets={this.datasets} />
      </svg>
    );
  }
}

export default ScatterPlot;
