import React, { Component } from "react";

class Button extends Component {
  /**
   * @param {string} dataName
   */
  onClick(dataName) {
    let regression = document.querySelector(`.regression__${dataName}`);

    if (+regression.getAttribute(`opacity`) === 0) {
      regression.setAttribute(`opacity`, 100);
    } else {
      regression.setAttribute(`opacity`, 0);
    }

    regression = null;
  }

  render() {
    return (
      <div>
        <button onClick={this.onClick}>Calculate Regression Lines</button>
        <input type="text" placeholder="Choose a degree"></input>
      </div>
    );
  }
}

export default Button;
