import React, { Component } from 'react';
import * as d3 from "d3";
import Axis from "../Axis/Axis"
import DataVis from "../DataVis/DataVis"
import Regression from "../Regression/Regression"

class ScatterPlot extends Component {
  constructor(props) {
    super(props);
    this.height = 400;
    this.padding  = 30;
    this.width= 400;
    this.xScale = this.getScale(
      this.padding,
      this.width - this.padding,
      this.getMax(`x`, this.props.data)
    );
    this.yScale = this.getScale(
      this.height - this.padding,
      this.padding,
      this.getMax(`y`, this.props.data)
    );
  }

  getScale(rangeStart, rangeEnd, max) {
    return d3.scaleLinear()
      .domain([0, max])
      .range([rangeStart, rangeEnd]);
  }

  getMax(dimension, data) {
    const max = []

    Array.from(data).forEach(set => {
      max.push(d3.max(set, function(d) { return d[dimension]; }))
    });
    return Math.max(...max)
  }

  render() {
    return (
      <svg width={this.width} height={this.height} ref={el => this.svgEl = el}>
        <Axis scale={this.xScale} type="x" height={this.height} padding={this.padding} />
        <Axis scale={this.yScale} type="y" height={this.height} padding={this.padding} />
        <DataVis data={this.props.data.dataset1} scales={ {x: this.xScale, y: this.yScale} } />
        <DataVis data={this.props.data.dataset2} scales={ {x: this.xScale, y: this.yScale} } />
        <DataVis data={this.props.data.dataset3} scales={ {x: this.xScale, y: this.yScale} } />
        <Regression data={this.props.data.dataset1} scales={ {x: this.xScale, y: this.yScale} } />
        <Regression data={this.props.data.dataset2} scales={ {x: this.xScale, y: this.yScale} } />
        <Regression data={this.props.data.dataset3} scales={ {x: this.xScale, y: this.yScale} } />
      </svg>
    );
  }
}

export default ScatterPlot;
