import React, { Component } from 'react';
import * as d3 from "d3";

class DataVis extends Component {
  constructor(props) {
    super(props);
    this.groupRef = React.createRef();
  }

  componentDidMount() {
    this.drawCircles()
  }

  drawCircles() {
    const xScale = d3.scaleLinear().domain([0,this.props.maxX]).range([30, 400]);
    const yScale = d3.scaleLinear().domain([0,this.props.maxY]).range([400, 30]);

    d3.select(this.groupRef.current)
      .selectAll("circle")
      .data(this.props.data)
      .enter()
      .append("circle")
      .attr("cx", function(d) {
        return xScale(d.x);
      })
      .attr("cy", function(d) {
        return yScale(d.y);
      })
      .attr("r", 3)
      .attr("fill", this.props.color);
}

  render() {
    return (
      <g ref={this.groupRef} className="test"></g>
    );
  }

}

export default DataVis;
