import React, { Component } from 'react';
import * as d3 from "d3";

class DataVis extends Component {
  constructor(props) {
    super(props);
    this.groupRef = React.createRef();
    this.groupEl = this.groupRef.current;
  }

  componentDidMount() {
    this.drawCircles()
  }

  drawCircles() {
    d3.select(this.groupEl).selectAll("circle")
      .data(this.props.data)
      .enter()
      .append("circle")
      .attr("cx", function(d) {
            return this.props.scales.x(d.x);
      })
      .attr("cy", function(d) {
            return this.props.scales.y(d.y);
      })
      .attr("r", 1)
      .attr("fill", "aqua");
  }

  render() {
    return (
      <g ref={this.groupRef}></g>
    );
  }

}

export default DataVis;
