import React, { Component } from 'react';
import * as d3 from "d3";
import Axis from "../Axis/Axis"
import DataVis from "../DataVis/DataVis"
import Regression from "../Regression/Regression"

class ScatterPlot extends Component {
  constructor(props) {
    super(props);
    this.height = 400;
    this.padding  = 30;
    this.width= 400;
    this.maxX = this.getMax(`x`, this.props.data)
    this.maxY = this.getMax(`y`, this.props.data)

    this.xScale = d3.scaleLinear()
      .domain([0, this.props.maxX])
      .range([this.props.padding, this.props.width - this.props.padding]);
    this.yScale = d3.scaleLinear()
      .domain([0, this.props.maxY])
      .range([this.props.height - this.props.padding, this.props.padding]);
  }

  getMax(dimension, data) {
    const max = []

    Object.entries(data).forEach(set => {
      max.push(d3.max(set[1], function(d) { return d[dimension]; }))
    });

    return d3.max(max)
  }

  render() {
    return (
      <svg width={this.width} height={this.height} style={{background: "grey"}}>
        <Axis scale={this.xScale} dimension="x" height={this.height} padding={this.padding} width={this.width} maxX={this.maxX} maxY={this.maxY} />
        <Axis scale={this.yScale} dimension="y" height={this.height} padding={this.padding} width={this.width} maxX={this.maxX} maxY={this.maxY} />
        <DataVis data={this.props.data.dataset1} height={this.height} padding={this.padding} xScale={this.xScale} yScale={this.yScale}
          width={this.width} maxX={this.maxX} maxY={this.maxY} color="aqua" />
        <DataVis data={this.props.data.dataset2} height={this.height} padding={this.padding} xScale={this.xScale} yScale={this.yScale}
          width={this.width} maxX={this.maxX} maxY={this.maxY} color="red" />
        <DataVis data={this.props.data.dataset3} height={this.height} padding={this.padding} xScale={this.xScale} yScale={this.yScale}
          width={this.width} maxX={this.maxX} maxY={this.maxY} color="green" />
        <Regression data={this.props.data.dataset1} />
        <Regression data={this.props.data.dataset2} />
        <Regression data={this.props.data.dataset3} />
      </svg>
    );
  }
}

export default ScatterPlot;
