import React, { Component } from 'react';
import * as d3 from "d3";
import { getMax } from "../utils/d3Utils"

class DataVis extends Component {

  drawCircles() {
    const xMax = getMax(`x`, this.props.data)
    const yMax = getMax(`y`, this.props.data)
    const xScale = d3.scaleLinear().domain([0,xMax]).range([30, 400]);
    const yScale = d3.scaleLinear().domain([0,yMax]).range([400, 30]);

    d3.select(this.groupEl)
      .selectAll("circle")
      .data(this.props.data)
      .enter()
      .append("circle")
      .attr("cx", function(d) {
        return xScale(d.x);
      })
      .attr("cy", function(d) {
        return yScale(d.y);
      })
      .attr("r", 3)
      .attr("fill", "aqua");
}

  render() {
    if (this.props.data) {
      this.drawCircles()
    }
    return (
      <g ref={el => this.groupEl = el} className="test"></g>
    );
  }

}

export default DataVis;
