import React, { Component } from 'react';
import * as d3 from "d3";
import Axis from "../Axis/Axis"
import DataVis from "../DataVis/DataVis"
import Regression from "../Regression/Regression"

class ScatterPlot extends Component {
  constructor(props) {
    super(props);
    this.height = 400;
    this.padding  = 30;
    this.width= 400;
  }

  getMax(dimension, data) {
    const max = []

    Array.from(data).forEach(set => {
      max.push(d3.max(set, function(d) { return d[dimension]; }))
    });
    return Math.max(...max)
  }

  render() {
    return (
      <svg width={this.width} height={this.height}>
        <Axis scale={this.xScale} type="x" height={this.height} padding={this.padding} width={this.width} data={this.props.data} />
        <Axis scale={this.yScale} type="y" height={this.height} padding={this.padding} width={this.width} />
        <DataVis data={this.props.data.dataset1} />
        <DataVis data={this.props.data.dataset2} />
        <DataVis data={this.props.data.dataset3} />
        <Regression data={this.props.data.dataset1} />
        <Regression data={this.props.data.dataset2} />
        <Regression data={this.props.data.dataset3} />
      </svg>
    );
  }
}

export default ScatterPlot;
