import React, { Component } from 'react';
import * as d3 from "d3";
import { getMax } from "../utils/d3Utils"

class Axis extends Component {
  getScale(rangeStart, rangeEnd, max) {
    return d3.scaleLinear()
      .domain([0, max])
      .range([rangeStart, rangeEnd]);
  }

  drawAxis() {
    var xMax = getMax(`x`, this.props.data)
    var yMax = getMax(`y`, this.props.data)
    var xScale = d3.scaleLinear()
      .domain([0, xMax])
      .range([this.props.padding, this.props.width - this.props.padding]);
    var yScale = d3.scaleLinear()
      .domain([0, yMax])
      .range([this.props.height - this.props.padding, this.props.padding]);
    let axis, xTranslate, yTranslate

    if (this.props.type === `x`) {
      axis = d3.axisBottom(xScale)
      xTranslate = 0
      yTranslate = this.props.height - this.props.padding
    } else if (this.props.type === `y`) {
      axis = d3.axisLeft(yScale)
      xTranslate = this.props.padding
      yTranslate = 0
    }

    d3.select(this.groupEl)
      .attr(`transform`, `translate(${xTranslate}, ${yTranslate})`)
      .call(axis);
  }

  render() {
    /* eslint-disable no-console */
    console.log(this.props.data)
    if (Object.entries(this.props.data).length !== 0) {
      this.drawAxis()
    }
    return (
      <g ref={el => this.groupEl = el}></g>
    );
  }

}

export default Axis;
