import React, { Component } from "react";

class Button extends Component {
  /**
   * @param {string} dataName
   */
  onClick(dataName) {
    let regression = document.querySelector(`.regression__${dataName}`);

    /* eslint-disable no-console */
    console.log(regression.opacity === 0)
    /* eslint-disable no-console */
    console.log(regression.opacity)

    if (+regression.opacity === 0) {
      regression.setAttribute(`opacity`, 100);
    } else {
      regression.setAttribute(`opacity`, 0);
    }

    regression = null;
  }

  render() {
    return (
      <button onClick={this.onClick.bind(this, this.props.name)}>Regression for {this.props.name}</button>
    );
  }
}

export default Button;
