import React, { Component } from "react";
import * as d3 from "d3";

class Legend extends Component {
  constructor(props) {
    super(props);
    this.groupRef = React.createRef();
    this.xPos = 40;
    this.yPos = 20;
  }

  componentDidMount() {
    this.drawLegend();
  }

  /**
   * @param {string} dataName
   */
  onClick(dataName) {
    let dataVis = d3.select(`group__${dataName}`);

    console.log(`test`);
    dataVis.setAttribute(`hidden`, `false`);
    dataVis.removeAttribute(`hidden`);

    dataVis = null;
  }

  drawLegend() {
    const legend = d3.select(this.groupRef.current);
    let x = this.xPos;

    Object.keys(this.props.datasets).forEach((key) => {
      /* eslint-disable no-console */
      console.log(key)
      legend.append(`g`)
        .on(`click`, this.onClick.bind(this, key));

      legend.append(`circle`)
        .attr(`cx`, x)
        .attr(`cy`, this.yPos)
        .attr(`r`, 10)
        .attr(`fill`, this.props.datasets[key].color);

      legend.append(`text`)
        .attr(`x`, x + 50)
        .attr(`y`, this.yPos)
        .text(key)
        .attr(`alignment-baseline`,`middle`);

      x += 10;
    });
}

  render() {
    return (
      <g ref={this.groupRef} className="legend">
      </g>
    );
  }

}

export default Legend;
