import React, { Component } from 'react';
import * as d3 from "d3";

class Axis extends Component {
  constructor(props) {
    super(props);
  }

  getMax(dimension, data) {
    const max = []

    Array.from(data).forEach(set => {
      max.push(d3.max(set, function(d) { return d[dimension]; }))
    });
    return Math.max(...max)
  }

  getScale(rangeStart, rangeEnd, max) {
    return d3.scaleLinear()
      .domain([0, max])
      .range([rangeStart, rangeEnd]);
  }

  drawAxis() {
    if (this.props.data) {
      var xScale = d3.scaleLinear().domain([0,400]).range([30, 400-30]);
      var yScale = d3.scaleLinear().domain([0,400]).range([400-30, 30]);
      let axis, xTranslate, yTranslate

      /* eslint-disable no-console */
      /* eslint-disable no-console */
      console.log(`HALALLAL`)
      console.log(yScale(200))

      if (this.props.type === `x`) {
        axis = d3.axisBottom(xScale)
        xTranslate = 0
        yTranslate = this.props.height - this.props.padding
      } else if (this.props.type === `y`) {
        axis = d3.axisLeft(yScale)
        xTranslate = this.props.padding
        yTranslate = 0
      }

      d3.select(this.groupEl)
        .attr(`transform`, `translate(${xTranslate}, ${yTranslate})`)
        .call(axis);
    }
  }

  render() {
    this.drawAxis()
    return (
      <g ref={el => this.groupEl = el}></g>
    );
  }

}

export default Axis;
