import React, { Component } from 'react';
import * as d3 from "d3";
import regression from 'regression';

class Regression extends Component {
  constructor(props) {
    super(props);
    this.pathRef = React.createRef();
    this.degree = 3;
  }

  componentDidMount() {
    this.drawRegression(this.props.data)
  }

  predictY(coefficients, x) {
    let y = 0

    for(let i = 0; i < coefficients.length; i++) {
      y += Math.pow(x, i) * coefficients[i]
    }

    return y
  }

  getTransformedDataset(dataset) {
    const transformedDataset = []

    dataset.forEach((pointObj) => {
      transformedDataset.push([pointObj.x, pointObj.y])
    })

    return transformedDataset
  }

  drawRegression(dataset) {
    let xScale = d3.scaleLinear()
      .domain([0, this.props.maxX])
      .range([this.props.padding, this.props.width - this.props.padding]);
    let yScale = d3.scaleLinear()
      .domain([0, this.props.maxY])
      .range([this.props.height - this.props.padding, this.props.padding]);

    const coefficients = regression.polynomial(this.getTransformedDataset(dataset), { order: this.degree });
    /* eslint-disable no-console */
    console.log(coefficients)
    const lineGenerator = d3.line()
      .x(function (d) {
        return xScale(d.x);
      })
      .y(function (d) {
        return yScale(
          (this.predictY(coefficients, d.x))
        );
      })
      .curve(d3.curveBasis);

    const pathData = lineGenerator(dataset);

    d3.select(this.pathRef.current).attr("class", "line")
      .attr("d", pathData).attr("stroke", "black");
  }

  render() {
    return (
      <path ref={this.pathRef}></path>
    );
  }

}

export default Regression;
