import React, { Component } from 'react';
import * as d3 from "d3";

class DataVisulisation extends Component {
  constructor(props) {
    super(props);
    this.groupRef = React.createRef();
  }

  componentDidMount() {
    this.drawCircles();
  }

  drawCircles() {
    const scales = this.props.scalesInstance.getScales();

    const dataVis = d3.select(this.groupRef.current);
    dataVis.attr(`class`, `group__data`)
      .selectAll(`circle`)
      .data(this.props.data)
      .enter()
      .append(`circle`)
      .attr(`cx`, function(d) {
        return scales.x(d.x);
      })
      .attr(`cy`, function(d) {
        return scales.y(d.y);
      })
      .attr(`r`, 3)
      .attr(`fill`, this.props.color);

    dataVis.append(`circle`)
      .attr(`class`, `group__legend`)
      .attr(`cx`, 50)
      .attr(`cy`, 50)
      .attr(`r`, 10)
      .attr(`fill`, this.props.color)
      .on(`click`, function() {
        console.log(`test`);
      });

}

  render() {
    return (
      <g ref={this.groupRef} className="test">
      </g>
    );
  }

}

export default DataVisulisation;
